{!REQUIRESCRIPT("/soap/ajax/8.0/connection.js")} 

var connection = sforce.connection; 
var Opportunity = new sforce.SObject("Opportunity"); 

if (Opportunity != "Awaiting Decision") 

{ 

var opp = new sforce.SObject("Opportunity"); 
opp.Id = "{!Opportunity.Id}"; 
opp.StageName = ("Awaiting Decision"); 
connection.update([opp]); 
window.top.location.href = window.top.location.href; 
window.parent.location.replace('/00T/e?&who_id={!Contact.Id}&what_id={!Opportunity.Id}&tsk4={!Today}&tsk10=Call&tsk5=Call {!Opportunity.Name}&00N50000001HEKm={!Opportunity.LeadSource}'); 
} 
else 
{ 
alert("Error"); 
}
