// Clean String values with \& and \# 
function cleanString(street){ 
if(street != null){ 
var street = street.replace(/\&/g,"%26"); 
var street = street.replace(/\#/g,"%23"); 
var street = street.replace(/\n/g," - "); 
return street; 
} 
else return ""; 
}
